INSERT INTO `galeria` VALUES (1,'20140613_Spektrumanalizatoros_magaslegkori_ballonozas','\n		<div class=\"galeria\">\n			<a class=\"linkbluebig\" href=\"https://plus.google.com/photos/105640944111891267601/albums/6024396873985292481?authkey=CNP_n7DQ2OnNUQ\" onclick=\"window.open(this.href); return false\"><img class=\"galeriapic\" src=\"/smog1/pictures/galeria/20140613_Spektrumanalizatoros_magaslegkori_ballonozas_little.jpg\" width=\"360\" height=\"270\" alt=\"Spektrumanalizátoros magaslégköri ballonozás\" title=\"Spektrumanalizátoros magaslégköri ballonozás\"></a>\n			<h2 class=\"galerytitle\"><a class=\"linkbluebig\" href=\"https://plus.google.com/photos/105640944111891267601/albums/6024396873985292481?authkey=CNP_n7DQ2OnNUQ\" onclick=\"window.open(this.href); return false\">Spektrumanalizátoros magaslégköri ballonozás</a></h2>\n			<p class=\"galerytext\">A ballon eresztés pillanatairól készült képek vannak ebben az albumban. A ballon engedés 2014. június 13-án\n					pénteken 10:30 történt. A ballon felküldésének célja, hogy megmérjük 400-800 MHz-ig a rádiófrekvenciás spektrumot.</p>\n			<p class=\"date\">2014.06.13.</p>\n			<a class=\"googlepic\" href=\"https://plus.google.com/photos/105640944111891267601/albums/6024396873985292481?authkey=CNP_n7DQ2OnNUQ\" onclick=\"window.open(this.href); return false\"><img src=\"/pictures/icons/arrow.png\" width=\"66\" height=\"50\" alt=\"nyíl\" title=\"nyíl\"></a>\n		</div>\n'),(2,'20140630_Spektrumanalizator_magaslegkori_ballonon_2._menet','\n		<div class=\"galeria\">\n			<a class=\"linkbluebig\" href=\"https://plus.google.com/photos/105640944111891267601/albums/6030729089984182417?authkey=CI2r4JLEqPTKOA\" onclick=\"window.open(this.href); return false\"><img class=\"galeriapic\" src=\"/smog1/pictures/galeria/20140630_Spektrumanalizator_magaslegkori_ballonon_2._menet_little.jpg\" width=\"360\" height=\"240\" alt=\"Spektrumanalizátor magaslégköri ballonon 2. menet\" title=\"Spektrumanalizátor magaslégköri ballonon 2. menet\">